INSERT [dbo].[Article] ([Id], [Titre], [Texte], [DateArticle], [LicenceId], [RefTypeArticleId], [SuppressorId], [isDeleted], [DateCreation], [DateModification]) VALUES (51, N'HB9HLM/CN2DX de retour ', N'Chers OMs, <br/>je vous annonce officiellement une petite réunion d''information sur le trafic en VHF avec diaporama sur les contests et trafic VHF, la date sera le 25 juillet 2008 à 20h00, le lieu sera Dar Bouazza au <b>Beach House</b>, une salle a été réservée, pour ce faire j''ai  besoin de savoir qui sera présent.<br /> je vous prie de bien vouloir vous inscrire afin d''organiser la salle en fonction du nombre de personne.<br />Cette réunion a pour but d''apporter des idées au OMs sur le trafic en VHF, montrer ce qui se réalise et passer un bon moment entre amis, merci d''avance de votre inscription et à très bientôt.<br /> 73 de André HB9HLM CN2DX', CAST(N'2008-06-21T00:00:00.000' AS DateTime), 313, 1, NULL, 0, CAST(N'2009-01-06T10:56:00.000' AS DateTime), CAST(N'2013-07-16T21:58:00.000' AS DateTime))
