INSERT [dbo].[Article] ([Id], [Titre], [Texte], [DateArticle], [LicenceId], [RefTypeArticleId], [SuppressorId], [isDeleted], [DateCreation], [DateModification]) VALUES (52, N'www.arram.org', N'<p>le site web est une id&eacute;e qui a longtemps &eacute;t&eacute; &eacute;voqu&eacute;e au sein de la communaut&eacute; OM du Maroc depuis l''apparition d''Internet.</p>  <p>Aujourd''hui, ce n''est plus une id&eacute;e mais une r&eacute;alit&eacute; palpable pour ne pas dire &quot;connectable&quot; gr&acirc;ce au d&eacute;vouement de quelques OM ''branch&eacute;s'' .</p>  <p>Un coin petites annonces OM est &agrave; l''&eacute;tude, si vous souhaitez vraiment le voir sur le site, faites le savoir &agrave; <strong>cn8ll@arram.org</strong></p>  ', CAST(N'2008-06-28T06:42:00.000' AS DateTime), 313, 1, NULL, 0, CAST(N'2009-01-06T10:56:00.000' AS DateTime), CAST(N'2013-07-16T21:58:00.000' AS DateTime))
