INSERT [dbo].[Article] ([Id], [Titre], [Texte], [DateArticle], [LicenceId], [RefTypeArticleId], [SuppressorId], [isDeleted], [DateCreation], [DateModification]) VALUES (53, N'Bizarre vous avez dit bizarre!!!', N'*<p>En feuilltant le dernier num&eacute;ro de Megahertz du mois de juillet, je lis qu''une association Radio Sans Fronti&egrave;re a organis&eacute; un exercice de secours dans la r&eacute;gion d''Al Hoceima. Cette initiative, louable en elle m&ecirc;me certes, a eu lieu au Maroc, qui compte pas moins de 580 OM, se distingue par l''absence totale des OM marocains. Je me demande o&ugrave; ont &eacute;t&eacute; les OM du Maroc lors de cette p&eacute;riode. En vaccances? sans doute pas mais cette marginalisation suscite chez moi une frustration profonde et une immense d&eacute;fiance vis &agrave; vis de ceux qui ont autoris&eacute; cette op&eacute;ration sur le sol national sans penser un instant &agrave; impliquer d''une mani&egrave;re ou d''une autre les OM du Maroc.</p>  <p>CN8LL</p>  <p>Adil</p>', CAST(N'2008-07-13T00:00:00.000' AS DateTime), 313, 1, NULL, 1, CAST(N'2009-01-06T10:56:00.000' AS DateTime), CAST(N'2013-07-16T21:57:00.000' AS DateTime))
