INSERT [dbo].[Article] ([Id], [Titre], [Texte], [DateArticle], [LicenceId], [RefTypeArticleId], [SuppressorId], [isDeleted], [DateCreation], [DateModification]) VALUES (58, N'Nouveaux Relais VHF', N'Afin d''encourager le trafic VHF et permettre au maximum d''OM''s de se contacter soit en fixe soit en mobile, ces dernières semaines ont vu l''apparition de plusieurs relais sur le territoire. Ainsi le réseau des relais VHF au Maroc s''étoffe avec la mise en place de nouveaux relais VHF grâce aux OM''s CN8. <br/> Les principales régions du Maroc sont concernées par l''implantation de ces nouveaux relais.  La liste à jour est dans la page <a href="http://www.arram.org/relaisetbalises.aspx" target="_self">relais et balises</a>.', CAST(N'2010-12-08T00:00:00.000' AS DateTime), 313, 1, NULL, 0, CAST(N'2010-12-08T18:15:00.000' AS DateTime), CAST(N'2010-12-26T10:39:00.000' AS DateTime))
