INSERT [dbo].[Article] ([Id], [Titre], [Texte], [DateArticle], [LicenceId], [RefTypeArticleId], [SuppressorId], [isDeleted], [DateCreation], [DateModification]) VALUES (59, N'Le Maroc actif sur VO-52', N'Bonjour à tous et meilleurs voeux pour 2011 pleine de santé et de dx.<br /><br />Ce matin, j''ai pu réaliser mon premier qso satelitte sur VO-52 avec l''ami Manuel EA7CTL. Je vous laisse imaginer ma joie de débutant, hi.<br />Les moyens utilisés étaient plûtot qrp.<BR/>TS-790E, antenne verticale x30 et un pc pour le log et la poursuite des sat.<br />Ce n''est pas bien difficile et à la portée de beaucoup d''entre nous.<br />Serait ce le premier qso sat depuis le Maroc? sur VO-52?', CAST(N'2010-12-26T00:00:00.000' AS DateTime), 313, 1, NULL, 0, CAST(N'2010-12-26T10:20:00.000' AS DateTime), CAST(N'2010-12-26T10:39:00.000' AS DateTime))
