INSERT [dbo].[Article] ([Id], [Titre], [Texte], [DateArticle], [LicenceId], [RefTypeArticleId], [SuppressorId], [isDeleted], [DateCreation], [DateModification]) VALUES (67, N'LE 8° NUMÉRO DE LA NEWSLETTER GADIRIE', N'<p>J''''ai le plaisir de vous annoncer la sortie du huiti&egrave;me num&eacute;ro de la newsletter des OM ''''s d''''Agadir.</p>  <p>Bravo &agrave; toute l''''&eacute;quipe gadirie pour la pers&eacute;v&eacute;rance et la t&eacute;nacit&eacute; dont ils font preuve pour sortir la newsletter.</p>  <p>Vous le trouverez i<a href="http://www.cn8vo.com/telechargement/Bulletin_Agadir_OMs_08.pdf">ci</a></p>  <p>&nbsp;</p>  <p>Bonne lecture &agrave; tous.</p>  <p>Adil</p>  <p>CN8LL</p>  <p>&nbsp;</p>', CAST(N'2012-02-20T13:31:00.000' AS DateTime), 313, 1, NULL, 0, CAST(N'2012-02-20T21:31:00.000' AS DateTime), CAST(N'2012-02-20T21:32:00.000' AS DateTime))
