INSERT [dbo].[Article] ([Id], [Titre], [Texte], [DateArticle], [LicenceId], [RefTypeArticleId], [SuppressorId], [isDeleted], [DateCreation], [DateModification]) VALUES (76, N'ARRAM.ORG sur mobile Android bientôt', N'Une application android reprenant quelques fonctionnalités du site est en cours de développement. Je recherche des testeurs équipés de téléphones android pour la tester. La version actuelle permet de consulter les derniers articles publiés sur le site arram.org.<br> <br> La prochaine version permettra de rechercher un radio amateur par son indicatif. Si vous avez des suggestions, je suis preneur.<br /> Contactez moi au cn8ll@arram.org<br/><br/>73 QRO', CAST(N'2014-03-30T00:00:00.000' AS DateTime), 313, 1, NULL, 0, CAST(N'2014-03-30T10:14:00.000' AS DateTime), CAST(N'2014-03-30T10:15:00.000' AS DateTime))
