INSERT [dbo].[Article] ([Id], [Titre], [Texte], [DateArticle], [LicenceId], [RefTypeArticleId], [SuppressorId], [isDeleted], [DateCreation], [DateModification]) VALUES (86, N'CQ World Wide Contest Phonie au Maroc', N'<p>A l''occasion du CQ WW DX Contest Phonie de cette ann&eacute;e, une &eacute;quipe de radioamateurs russes et une autre &eacute;quipe de radioamateurs espagnols ont d&eacute;barqu&eacute; au Maroc pour activer CN2AA du 25 au 26 octobre 2014.<br/><br>Site web : www.cn2aa.com<br/><br/></p><p>L''&eacute;quipe russe &eacute;tait compos&eacute;e de :</p><ul><li>DMITRY GORSHKOV UA2FB / CN2FB</li><li>VLADIMIR GUMENNIKOV UA2FF / CN2FF </li><li>ALEXEY TSYGONYAEV RN2FA / CN2FA </li><li>Oleg Zhukov R3FA / CN2OZ </li><li>ANDREY ZHUKOV RK7A / CN2BGB </li><li>YURI KHMELENKO RL3FT / CN2AA </li><li>SERGEY Dyachenko RX3APM / CN2PM </li><li>DMITRI Kryukov RA3CO / CN2CO </li><li>ROMAN Levichev RV3MA / CN2MA </li><li>RUSLAN BABAYAN UA3ASZ </li><li>OLEG LEBEDEV RO6L </li><li>EVGENY Koulechov RK3AD </li><li>PAVEL Bogachev RK4FD </li><li>ROMAN GERASIN RO4F </li><li>VALERI Kirilchik RK4FW </li><li>ANDREY Kotowski RW7K </li><li>ALEXEY SMECHNOV RG6G </li><li>IGOR BACHEVSKI RJ4P </li><li>DMITRY ZNIKHAREV RA9USU </li><li>ALEXEY POPOV RC6U </li><li>DMITRI Gouskov R3DCX </li></ul><p>L''&eacute;quipe espagnole &eacute;tait compos&eacute;e de :</p><ul><li>Gonzalez Guillen Antonuio&nbsp;&nbsp;&nbsp;&nbsp; EA5RM&nbsp;&nbsp; /&nbsp; CN2C</li><li>Julio Cesar Volpe O''Neil&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; EA5XX&nbsp;&nbsp; /&nbsp; CN2XX</li><li>Manuel German Piedehierro&nbsp; EA7AJR /&nbsp; CN2JR</li><li>Filloy Garcia Roberto&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; EA2RY&nbsp;&nbsp; /&nbsp; CN2RY</li></ul><p>Assist&eacute;es de CN8LR et CN8KD.</p>', CAST(N'2014-11-20T00:00:00.000' AS DateTime), 313, 1, NULL, 0, CAST(N'2014-11-20T14:11:00.000' AS DateTime), CAST(N'2014-11-20T14:23:00.000' AS DateTime))
