INSERT [dbo].[Article] ([Id], [Titre], [Texte], [DateArticle], [LicenceId], [RefTypeArticleId], [SuppressorId], [isDeleted], [DateCreation], [DateModification]) VALUES (91, N'Essais ATV avec CN8JQ et CN8LL', N'<p>On a avait fix&eacute; le rendez vous samedi 13 f&eacute;vrier 2014. A l''heure dite, CN8JQ se pr&eacute;sente au si&egrave;ge de l''ARRAM avec son mat&eacute;riel et ses antennes "Homemade".</p><p></p><p>Apr&egrave;s installation et branchement des a&eacute;riens, on recevait son &eacute;metteur ATV 1.2 Ghz avec son propre r&eacute;cepteur ATV. L''&eacute;metteur est install&eacute; &agrave; son QRA &agrave; Sal&eacute;.</p>', CAST(N'2014-11-26T00:00:00.000' AS DateTime), 313, 1, NULL, 0, CAST(N'2014-11-26T21:18:00.000' AS DateTime), CAST(N'2014-11-30T04:39:00.000' AS DateTime))
