INSERT [dbo].[Article] ([Id], [Titre], [Texte], [DateArticle], [LicenceId], [RefTypeArticleId], [SuppressorId], [isDeleted], [DateCreation], [DateModification]) VALUES (92, N'CN40 Spécial Marche verte', N'<p>Dans le cadre des festivit&eacute;s comm&eacute;morant le 40&egrave;me&nbsp; anniversaire de la Marche Verte, les radioamateurs Marocains, en collaboration avec l''Association Royale des Radioamateurs du Maroc, pourront c&eacute;l&eacute;brer &agrave; leur tour cet heureux &eacute;v&eacute;nement.</p><p>Cette comm&eacute;moration aura lieu du 03 au 08 Novembre 2015.</p><p>Ils pourront utiliser l''indicatif CN40XXX &agrave; la place de leur indicatif habituel pour tous leurs contacts &eacute;tablis pendant cette p&eacute;riode.</p><p></p><p>Par exemple; CN8MC sera CN40MC.</p><p></p><p>Pour rappel, la marche verte a &eacute;t&eacute; un moment marquant dans l''histoire du royaume. Une marche pacifique pour la r&eacute;cup&eacute;ration des provinces sahariennes a &eacute;t&eacute; r&eacute;alis&eacute;e par 350000 marcheurs non arm&eacute;s sinon d''un saint coran.</p><p></p>', CAST(N'2015-10-29T00:00:00.000' AS DateTime), 313, 1, NULL, 0, CAST(N'2015-10-29T05:32:00.000' AS DateTime), CAST(N'2015-11-09T02:11:00.000' AS DateTime))
