INSERT [dbo].[Article] ([Id], [Titre], [Texte], [DateArticle], [LicenceId], [RefTypeArticleId], [SuppressorId], [isDeleted], [DateCreation], [DateModification]) VALUES (93, N'Célébration du 40eme anniversaire de la marche verte', N'<img alt="Le 40eme anniversaire de la Marche Verte" height="197" src="http://www.arram.org/Illustrations/40emeMV01.jpg" width="350" /><p>Les radio amateurs marocains ont c&eacute;l&eacute;br&eacute;, le 6 novembre, le 40eme anniversaire de la marche verte au si&egrave;ge du radio club CN8MC.</p><p></p><p>Ont particip&eacute; &agrave; cet heureux &eacute;v&eacute;nement les radio amateurs suivants:&nbsp;</p><ul><li>CN8LR</li><li>CN8IT</li></ul>', CAST(N'2015-06-11T00:00:00.000' AS DateTime), 319, 1, NULL, 1, CAST(N'2015-11-09T02:07:00.000' AS DateTime), CAST(N'2018-09-02T13:00:45.910' AS DateTime))
