INSERT [dbo].[Article] ([Id], [Titre], [Texte], [DateArticle], [LicenceId], [RefTypeArticleId], [SuppressorId], [isDeleted], [DateCreation], [DateModification]) VALUES (94, N'CN90IARU : Célébration du 90ème anniversaire de l''''IARU', N'<p>Le radio-club CN8MC a été choisi par l''IARU pour participer à cet évenement.</P><P>A cet effet, la station opérera jusqu''au 31 Décembre 2015 avec l''indicatif CN90IARU. Tous les OM marocains sont invités à passer au siège à Rabat afin d''opérer la station CN90IARU afin de donner des opportunités aux OM du monde entier pour réaliser le contact avec une station célébrant cet évenement.</P><P>Toutes les stations qui ont réalisé 10 contacts avec dix stations portant l''indicatif XX90IARU recevront de la part de l''IARU un diplôme. </P>', CAST(N'2015-11-16T00:00:00.000' AS DateTime), 491, 1, NULL, 0, CAST(N'2015-11-17T19:37:00.000' AS DateTime), CAST(N'2015-11-17T19:37:00.000' AS DateTime))
